import React, { useState } from 'react';
import Head from 'next/head';
import AppLayout from '../components/AppLayout';
import { Form, Input, Checkbox, Button } from 'antd';

const Signup = () => {

    const [id, setId] = useState('');
    const [nickname, setNickname] = useState('');
    const [password, setPassword] = useState('');
    const [passwordCheck, setPasswordCheck] = useState('');
    const [term, setTerm] = useState(false);

    const onSubmit = (e) => {
        e.preventDefault();
        if( password !== passwordCheck ){
            return setPasswordError(true);
        }
        console.log({
            id,
            nickname,
            password,
            paswordCheck,
            term,
        });
    };

    const onChangeId = (e) => {
        setId(e.target.value);
    };

    const onChangeNickname = (e) => {
        setNickname(e.target.value);
    };

    const onChangePassword = (e) => {
        setPassword(e.target.value);
    };

    const onChangePasswordCheck = (e) => {
        setPasswordCheck(e.target.value);
    };

    const onChangeTerm = (e) => {
        setTerm(e.target.checked);
    };

    return (
        <>
        <Head>
            <title>NodeBird</title>
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/4.3.5/antd.css" />
        </Head>
        <AppLayout>
            <Form onSubmit={onSubmit} style={{ padding: 10 }}>
                <div>
                    <label hmltFor="user-id">아이디</label>
                    <br />
                    <Input name="user-id" value={id} required onChange={onChangeId} />
                </div>
                <div>
                    <label hmltFor="user-nickname">닉네임</label>
                    <br />
                    <Input name="user-nickname" value={nickname} required onChange={onChangeNickname} />
                </div>
                <div>
                    <label hmltFor="user-password">비밀번호</label>
                    <br />
                    <Input type="passowrd" name="user-password" value={password} required onChange={onChangePassword} />
                </div>
                <div>
                    <label hmltFor="user-password-check">비밀번호 확인</label>
                    <br />
                    <Input type="passowrd" name="user-password-check" value={passwordCheck} required onChange={onChangePasswordCheck} />
                </div>
                <div>
                    <Checkbox name="user-term" value={term} onChange={onChangeTerm}>회원가입을 동의합니다.</Checkbox>
                </div>
                <div>
                    <Button type="primary" htmlType="submit">가입하기</Button>
                </div>
            </Form>
        </AppLayout>
        </>
    );
}

export default Signup;
